import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-parent',
  templateUrl: './parent.component.html',
  styleUrls: ['./parent.component.css']
})
export class ParentComponent implements OnInit {

  allTutors: any;
  allBooks: any;
  loggedIn = false;

  viewTutors = true;
  viewBooks = false;

  email = "";
  password = "";




  constructor(protected userService: UserService) {

    this.userService.getAllTutors().subscribe((data) => {
      this.allTutors = data;
      console.log(data);

    });

    this.userService.getAllBooks().subscribe((data) => {
      this.allBooks = data;
      console.log(data);

    });
  }

  toggleTutors() {
    this.viewTutors = true;
    this.viewBooks = false;

  }

  toggleBooks() {
    this.viewBooks = true;
    this.viewTutors = false;
  }

  requestDemo(user: any) {

    console.log("Request Demo:", user);

  }

  bookTutor(user: any) {
    console.log(user);
  }


  async loginUser() {
    this.userService.loginUser(this.email, this.password);

    this.userService.loginUser(this.email, this.password);

    setTimeout(() => {
      console.log("11");

    }, 3000)

    console.log("Active User in Login:", this.userService.activeUser);


    Object.entries(this.userService.activeUser).forEach(([key, value]) => {
      console.log(key, value);
      sessionStorage.setItem(key, <string>value);

      this.loggedIn = true;

    });
  }

  ngOnInit(): void {
    this.userService.getAllTutors().subscribe((data) => {
      this.allTutors = data;
      console.log(data);

    });

    this.userService.getAllBooks().subscribe((data) => {
      this.allBooks = data;
      console.log(data);

    });
  }
}
